document.addEventListener("DOMContentLoaded",function(){window.showSection=function(e,t){let c=document.querySelectorAll(".form-section"),o=document.querySelectorAll(".toggle-btn");c.forEach(e=>e.classList.remove("active")),o.forEach(e=>e.classList.remove("active")),document.getElementById(e).classList.add("active"),t.classList.add("active")}});