jQuery(document).ready(function () {
	jQuery(".comments-area").each(function(i){
        jQuery(this).addClass("footer-comment_" + i);
    });
    jQuery('.addon-comments ul.comment-list').each(function(){
    	var per_page = wpc_ajax_object.per_page;
    	if (per_page == '' || per_page == null) {
    		per_page = '6';
    	}
	    if(jQuery(this).children('li').length>per_page){
	        jQuery(".addon-comments ul.comment-list li").hide();
	        jQuery(this).children('li:lt(5)').show();
	        jQuery("#loadMore_new").show();
	    }
	  });   
	  jQuery('.addon-comments ul.comment-list').on("click",'.loadMore',function (){
	    jQuery(this).parent('.addon-comments .comment-list').children('li:gt(4)').show(); // use gt instead of lt
	    jQuery(this).removeClass('loadMore').addClass('loadLess'); 
	  });
	  jQuery('.addon-comments ul.comment-list').on("click",'.loadLess',function () {
	    jQuery(this).parent('.addon-comments .comment-list').children('li:gt(4)').hide(); // use gt instead of lt
	    jQuery(this).removeClass('loadLess').addClass('loadMore'); 
	  });
	  if(document.getElementById("addon-comments-id") !== null){
	    jQuery("body").addClass('hide-default-cm');
	  }
});